'use strict';

/**
* Binomial distributed pseudorandom numbers.
*
* @module @stdlib/math/base/random/binomial
*
* @example
* var binomial = require( '@stdlib/math/base/random/binomial' );
*
* var v = binomial( 20, 0.4 );
* // returns <number>
*
* @example
* var factory = require( '@stdlib/math/base/random/binomial' ).factory;
*
* var binomial = factory( 90, 0.8, {
*     'seed': 297
* });
*
* var v = binomial();
* // returns <number>
*/

// MODULES //

var setReadOnly = require( '@stdlib/utils/define-read-only-property' );
var binomial = require( './binomial.js' );
var factory = require( './factory.js' );


// MAIN //

setReadOnly( binomial, 'factory', factory );


// EXPORTS //

module.exports = binomial;
