'use strict';

/**
* Cauchy distribution pseudorandom numbers.
*
* @module @stdlib/math/base/random/cauchy
*
* @example
* var rcauchy = require( '@stdlib/math/base/random/cauchy' );
*
* var v = rcauchy( 0.5, 1.0 );
* // returns <number>
*
* @example
* var factory = require( '@stdlib/math/base/random/cauchy' ).factory;
*
* var rcauchy = factory( 3.0, 2.0, {
* 	'seed': 297
* });
*
* var v = rcauchy();
* // returns <number>
*/

// MODULES //

var setReadOnly = require( '@stdlib/utils/define-read-only-property' );
var cauchy = require( './cauchy.js' );
var factory = require( './factory.js' );


// METHODS //

setReadOnly( cauchy, 'factory', factory );


// EXPORTS //

module.exports = cauchy;
