'use strict';

// MODULES //

var sqrt = require( '@stdlib/math/base/special/sqrt' );


// MAIN //

/**
* Returns a pseudorandom number drawn from a chi distribution.
*
* @private
* @param {Function} rchisq - PRNG for chi-square distributed numbers
* @param {PositiveNumber} k - degrees of freedom
* @returns {number} pseudorandom number
*/
function sample( rchisq, k ) {
	return sqrt( rchisq( k ) );
} // end FUNCTION sample()


// EXPORTS //

module.exports = sample;
