'use strict';

/**
* Raised cosine distributed pseudorandom numbers.
*
* @module @stdlib/math/base/random/cosine
*
* @example
* var cosine = require( '@stdlib/math/base/random/cosine' );
*
* var v = cosine( 0.5, 1.0 );
* // returns <number>
*
* @example
* var factory = require( '@stdlib/math/base/random/cosine' ).factory;
* var cosine = factory( 3.0, 2.0, {
*     'seed': 297
* });
*
* var v = cosine();
* // returns <number>
*
* @example
* var factory = require( '@stdlib/math/base/random/cosine' ).factory;
* var cosine = factory({
*     'seed': 297
* });
*
* var v = cosine( 3.0, 2.0 );
* // returns <number>
*/

// MODULES //

var setReadOnly = require( '@stdlib/utils/define-read-only-property' );
var cosine = require( './cosine.js' );
var factory = require( './factory.js' );


// MAIN //

setReadOnly( cosine, 'factory', factory );


// EXPORTS //

module.exports = cosine;
