'use strict';

/**
* Erlang distributed pseudorandom numbers.
*
* @module @stdlib/math/base/random/erlang
*
* @example
* var rerlang = require( '@stdlib/math/base/random/erlang' );
*
* var v = rerlang( 3, 2.5 );
* // returns <number>
*
* @example
* var factory = require( '@stdlib/math/base/random/erlang' ).factory;
*
* var rerlang = factory( 8, 5.9, {
* 	'seed': 297
* });
*
* var v = rerlang();
* // returns <number>
*/

// MODULES //

var setReadOnly = require( '@stdlib/utils/define-read-only-property' );
var erlang = require( './erlang.js' );
var factory = require( './factory.js' );


// METHODS //

setReadOnly( erlang, 'factory', factory );


// EXPORTS //

module.exports = erlang;
