'use strict';

var rf = require( './../lib' );

var seed;
var rand;
var i;

// Generate pseudorandom numbers...
console.log( '\nseed: %d', rf.SEED );
for ( i = 0; i < 100; i++ ) {
	console.log( rf( 4.0, 2.0 ) );
}

// Create a new pseudorandom number generator...
seed = 1234;
rand = rf.factory( 3.0, 3.0, {
	'seed': seed
});
console.log( '\nseed: %d', seed );
for ( i = 0; i < 100; i++ ) {
	console.log( rand() );
}

// Create another pseudorandom number generator using a previous seed...
rand = rf.factory( 4.0, 2.0, {
	'seed': rf.SEED
});
console.log( '\nseed: %d', rf.SEED );
for ( i = 0; i < 100; i++ ) {
	console.log( rand() );
}
