'use strict';

/**
* Gamma distributed pseudorandom numbers.
*
* @module @stdlib/math/base/random/gamma
*
* @example
* var rgamma = require( '@stdlib/math/base/random/gamma' );
*
* var v = rgamma( 2.0, 3.0 );
* // returns <number>
*
* @example
* var factory = require( '@stdlib/math/base/random/gamma' ).factory;
*
* var rgamma = factory( 3.0, 2.0, {
* 	'seed': 297
* });
*
* var v = rgamma();
* // returns <number>
*/

// MODULES //

var setReadOnly = require( '@stdlib/utils/define-read-only-property' );
var gamma = require( './gamma.js' );
var factory = require( './factory.js' );


// METHODS //

setReadOnly( gamma, 'factory', factory );


// EXPORTS //

module.exports = gamma;
