'use strict';

var geometric = require( './../lib' );

var seed;
var rand;
var i;

// Generate pseudorandom numbers...
console.log( '\nseed: %d', geometric.SEED );
for ( i = 0; i < 100; i++ ) {
	console.log( geometric( 0.5 ) );
}

// Create a new pseudorandom number generator...
seed = 1234;
rand = geometric.factory( 0.8, {
	'seed': seed
});
console.log( '\nseed: %d', seed );
for ( i = 0; i < 100; i++ ) {
	console.log( rand() );
}

// Create another pseudorandom number generator using a previous seed...
rand = geometric.factory( 0.5, {
	'seed': geometric.SEED
});
console.log( '\nseed: %d', geometric.SEED );
for ( i = 0; i < 100; i++ ) {
	console.log( rand() );
}
