'use strict';

// MODULES //

var setReadOnly = require( '@stdlib/utils/define-read-only-property' );
var isNumber = require( '@stdlib/utils/is-number' ).isPrimitive;
var isObject = require( '@stdlib/utils/is-object' );
var isnan = require( '@stdlib/utils/is-nan' ).isPrimitive;
var randu = require( '@stdlib/math/base/random/randu' ).factory;
var floor = require( '@stdlib/math/base/special/floor' );
var ln = require( '@stdlib/math/base/special/ln' );


// FACTORY //

/**
* Returns a pseudorandom number generator for generating geometric distributed random numbers.
*
* @param {Probability} [p] - success probability
* @param {Options} [opts] - function options
* @param {*} [opts.seed] - pseudorandom number generator seed
* @throws {TypeError} p argument must be numeric
* @throws {RangeError} p argument must be a number between 0 and 1
* @throws {TypeError} options must be an object
* @returns {Function} pseudorandom number generator
*
* @example
* var rgeom = factory( 0.5 );
* var v = rgeom();
* // returns <number>
*
* @example
* var rgeom = factory( 0.8, {
* 	'seed': 297
* });
* var v = rgeom();
* // returns <number>
*
* @example
* var rgeom = factory()
* var v = rgeom( 0.5 );
* // returns <number>
*/
function factory() {
	var nargs;
	var args;
	var opts;
	var rand;
	var ret;
	var p;

	args = arguments;
	nargs = args.length;
	if ( nargs === 0 ) {
		rand = randu();
	}
	else if ( nargs > 0 ) {
		if ( isObject( args[ 0 ] ) ) {
			opts = args[ 0 ];
			rand = randu( opts );
		} else {
			p = args[ 0 ];
			if ( !isNumber( p ) || isnan( p ) ) {
				throw new TypeError( 'invalid input argument. Second argument `p` must be a number primitive. Value: `' + p + '`.' );
			}
			if ( p < 0.0 || p > 1.0 ) {
				throw new RangeError( 'invalid input argument. Second argument `p` must be a probability. Value: `' + p + '`.' );
			}
			if ( nargs > 1 ) {
				opts = args[ 1 ];
				if ( !isObject( opts ) ) {
					throw new TypeError( 'invalid input argument. If supplied, third argument must be an options object. Value: `' + opts + '`.' );
				}
				rand = randu( opts );
			} else {
				rand = randu();
			}
		}
	}

	if ( p !== void 0 ) {
		ret = geometric1;
	}
	else {
		ret = geometric2;
	}
	setReadOnly( ret, 'NAME', 'geometric' );
	setReadOnly( ret, 'SEED', rand.SEED );
	setReadOnly( ret, 'PRNG', rand.PRNG );
	return ret;

	/**
	* Returns a random number drawn from a geometric distribution with bound parameter `p`.
	*
	* @private
	* @returns {number} pseudorandom number
	*/
	function geometric1() {
		var u = rand();
		return floor( ln( u ) /  ln( 1.0 - p) );
	} // end FUNCTION geometric1()

	/**
	* Returns a random number drawn from a geometric distribution with parameter `p`.
	*
	* @private
	* @param {Probability} p - success probability
	* @throws {TypeError} p argument must be numeric
	* @throws {RangeError} p argument must be a number between 0 and 1
	* @returns {number} pseudorandom number
	*/
	function geometric2( p ) {
		var u;
		if ( !isNumber( p ) ) {
			throw new TypeError( 'invalid input argument. Second argument `p` must be a number primitive. Value: `' + p + '`.' );
		}
		if ( p < 0.0 || p > 1.0 ) {
			throw new RangeError( 'invalid input argument. Second argument `p` must be a probability. Value: `' + p + '`.' );
		}
		u = rand();
		return floor( ln( u ) /  ln( 1.0 - p) );
	} // end FUNCTION geometric2()

} // end FUNCTION factory()


// EXPORTS //

module.exports = factory;
