'use strict';

/**
* Geometric distributed pseudorandom numbers.
*
* @module @stdlib/math/base/random/geometric
*
* @example
* var geometric = require( '@stdlib/math/base/random/geometric' );
*
* var v = geometric( 0.5 );
* // returns <NonNegativeInteger>
*
* @example
* var factory = require( '@stdlib/math/base/random/geometric' ).factory;
*
* var geometric = factory( 0.3, {
* 	'seed': 297
* });
*
* var v = geometric();
* // returns <NonNegativeInteger>
*/

// MODULES //

var setReadOnly = require( '@stdlib/utils/define-read-only-property' );
var geometric = require( './geometric.js' );
var factory = require( './factory.js' );


// METHODS //

setReadOnly( geometric, 'factory', factory );


// EXPORTS //

module.exports = geometric;
