'use strict';

// MODULES //

var tape = require( 'tape' );
var round = require( '@stdlib/math/base/special/round' );
var factory = require( './../lib/factory.js' );


// TESTS //

tape( 'main export is a function', function test( t ) {
	t.ok( true, __filename );
	t.equal( typeof factory, 'function', 'main export is a function' );
	t.end();
});

tape( 'the function returns a pseudorandom number generator (no seed)', function test( t ) {
	var geometric;
	var r;
	var i;

	// When binding distribution parameters...
	geometric = factory( 0.05 );
	for ( i = 0; i < 100; i++ ) {
		r = geometric();
		t.equal( typeof r, 'number', 'returns a number' );
	}

	// Without binding distribution parameters...
	geometric = factory();
	for ( i = 0; i < 100; i++ ) {
		r = geometric( 0.1 );
		t.equal( typeof r, 'number', 'returns a number' );
	}

	t.end();
});

tape( 'the function returns a seeded pseudorandom number generator', function test( t ) {
	var geometric1;
	var geometric2;
	var seed;
	var r1;
	var r2;
	var i;

	seed = round( Date.now() / 1000 );

	geometric1 = factory( 0.3, {
		'seed': seed
	});
	geometric2 = factory( 0.3, {
		'seed': seed
	});

	t.notEqual( geometric1, geometric2, 'separate generators' );

	for ( i = 0; i < 100; i++ ) {
		r1 = geometric1();
		r2 = geometric2();
		t.equal( r1, r2, 'both return same number' );
	}
	t.end();
});

tape( 'attached to the returned function is the generator name', function test( t ) {
	var geometric = factory();
	t.equal( geometric.NAME, 'geometric', 'has `NAME` property' );
	t.end();
});

tape( 'attached to the returned function is the generator seed', function test( t ) {
	var geometric = factory({
		'seed': 12345
	});
	t.equal( typeof geometric.SEED, 'number', 'has `SEED` property' );
	t.equal( geometric.SEED, 12345, 'equal to provided seed' );
	t.end();
});

tape( 'the function throws an error if the `p` value is not a number primitive', function test( t ) {
	var values;
	var i;

	values = [
		'5',
		null,
		true,
		undefined,
		NaN,
		[],
		function(){}
	];

	for ( i = 0; i < values.length; i++ ) {
		t.throws( badValue( values[i] ), TypeError, 'throws an error when provided '+values[i] );
	}
	t.end();

	function badValue( value ) {
		return function() {
			factory( value );
		};
	}
});

tape( 'the function throws an error if the `p` value is outside the interval [0,1]', function test( t ) {
	var values;
	var i;

	values = [
		-2.0,
		-1.0,
		-0.1,
		1.1,
		2.0
	];

	for ( i = 0; i < values.length; i++ ) {
		t.throws( badValue( values[i] ), RangeError, 'throws an error when provided '+values[i] );
	}
	t.end();

	function badValue( value ) {
		return function() {
			factory( value );
		};
	}
});

