tape( 'when called without arguments, the function returns a PRNG that returns `NaN` when provided a rate equal to `NaN`', function test( t ) {
	var geometric;
	var r;

	geometric = factory();
	r = geometric( NaN );

	t.strictEqual( isnan( r ), true, 'returns NaN' );
	t.end();
});

tape( 'when called without arguments, the function returns a PRNG that returns `NaN` when provided a probability argument which is not a probability', function test( t ) {
	var geometric;
	var r;

	geometric = factory();

	r = geometric( 3.14 );
	t.strictEqual( isnan( r ), true, 'returns NaN' );

	r = geometric( -1.0 );
	t.strictEqual( isnan( r ), true, 'returns NaN' );

	r = geometric( -3.14 );
	t.strictEqual( isnan( r ), true, 'returns NaN' );

	t.end();
});

tape( 'the function returns a PRNG for generating random numbers from a geometric distribution', opts, function test( t ) {
	var geometric;
	var rejected;
	var repeats;
	var pValue;
	var probs;
	var freq;
	var gpmf;
	var N;
	var p;
	var i;
	var j;
	var x;

	p = 0.67;
	gpmf = pmf( p );

	freq = new Array( 30 );
	probs = new Array( freq.length );
	for ( j = 0; j < freq.length; j++ ) {
		probs[ j ] = gpmf( j );
	}
	repeats = 200;
	N = 1e3;
	rejected = 0;
	for ( i = 0; i < repeats; i++ ) {
		geometric = factory( p );
		t.ok( true, 'seed: '+geometric.SEED );

		// Reset the `freq` array...
		for ( j = 0; j < freq.length; j++ ) {
			freq[ j ] = 0;
		}
		for ( j = 0; j < N; j++ ) {
			x = geometric();
			freq[ x ] += 1;
		}
		// Test using chi-square goodness-of-fit test:
		pValue = chi2gof( freq, probs, {
			'rescale': true,
			'simulate': true,
			'iterations': 500
		}).pValue;
		t.equal( typeof pValue, 'number', 'returns a p-value: '+pValue );
		if ( pValue < 0.05 ) {
			rejected += 1;
		}
	}
	// Account for small sample sizes and few repeats...
	t.ok( rejected / repeats < 0.10, 'null hypothesis (i.e., that numbers are drawn from Geometric('+p+') is rejected in less than 10% of cases ('+rejected+' of '+repeats+')' );
	t.end();
});
