'use strict';

// MODULES //

var setReadOnly = require( '@stdlib/utils/define-read-only-property' );
var isPositive = require( '@stdlib/utils/is-positive-number' ).isPrimitive;
var isNumber = require( '@stdlib/utils/is-number' ).isPrimitive;
var isObject = require( '@stdlib/utils/is-plain-object' );
var isnan = require( '@stdlib/utils/is-nan' );
var randu = require( '@stdlib/math/base/random/randu' );
var ln = require( '@stdlib/math/base/special/ln' );


// FACTORY //

/**
* Returns a pseudorandom number generator for generating random numbers from a Gumbel distribution.
*
* @param {number} [mu] - mean parameter
* @param {PositiveNumber} [beta] - scale parameter
* @param {Options} [opts] - function options
* @param {*} [opts.seed] - pseudorandom number generator seed
* @throws {TypeError} mu argument must be a number primitive
* @throws {TypeError} beta argument must be a positive number
* @throws {TypeError} options must be an object
* @returns {Function} pseudorandom number generator
*
* @example
* var gumbel = factory( 0.0, 1.0 );
* var v = gumbel();
* // returns <number>
*
* @example
* var gumbel = factory( -3.0, 0.5, {
* 	'seed': 297
* });
* var v = gumbel();
* // returns <number>
*/
function factory() {
	var nargs;
	var args;
	var beta;
	var opts;
	var rand;
	var ret;
	var mu;

	args = arguments;
	nargs = args.length;
	if ( nargs === 0 ) {
		rand = randu.factory();
	}
	else {
		if ( isObject( args[ 0 ] ) ) {
			opts = args[ 0 ];
			rand = randu.factory( opts );
		}
		else {
			mu = args[ 0 ];
			beta = args[ 1 ];
			if ( !isNumber( mu ) || isnan( mu ) ) {
				throw new TypeError( 'invalid input argument. `mu` must be a number primitive. Value: `' + mu + '`.' );
			}
			if ( !isPositive( beta ) ) {
				throw new TypeError( 'invalid input argument. `beta` must be a positive number. Value: `' + beta + '`.' );
			}
			if ( nargs > 2 ) {
				opts = args[ 2 ];
				if ( !isObject( opts ) ) {
					throw new TypeError( 'invalid input argument. If supplied, third argument must be an options object. Value: `' + opts + '`.' );
				}
				rand = randu.factory( opts );
			} else {
				rand = randu.factory();
			}
		}
	}

	if ( mu !== void 0 ) {
		ret = gumbel1;
	} else {
		ret = gumbel2;
	}
	setReadOnly( ret, 'NAME', 'gumbel' );
	setReadOnly( ret, 'SEED', rand.SEED );
	setReadOnly( ret, 'PRNG', rand );
	return ret;

	/**
	* Returns pseudorandom number drawn from a Gumbel distribution with bound parameters.
	*
	* @private
	* @param {number} mu - mean parameter
	* @param {PositiveNumber} beta - scale parameter
	* @returns {number} pseudorandom number
	*
	* @example
	* var v = gumbel1();
	* // returns <number>
	*/
	function gumbel1() {
		var u = rand();
		return mu - beta * ln( -ln( u ) );
	} // end FUNCTION gumbel1()

	/**
	* Returns pseudorandom number drawn from a Gumbel distribution.
	*
	* @private
	* @param {number} mu - mean parameter
	* @param {PositiveNumber} beta - scale parameter
	* @throws {TypeError} mu argument must be a number primitive
	* @throws {TypeError} beta argument must be a positive number
	* @returns {number} pseudorandom number
	*
	* @example
	* var v = gumbel2( 0.0, 2.0 );
	* // returns <number>
	*/
	function gumbel2( mu, beta ) {
		var u;
		if ( !isNumber( mu ) || isnan( mu ) ) {
			throw new TypeError( 'invalid input argument. `mu` must be a number primitive. Value: `' + mu + '`.' );
		}
		if ( !isPositive( beta ) ) {
			throw new TypeError( 'invalid input argument. `beta` must be a positive number. Value: `' + beta + '`.' );
		}
		u = rand();
		return mu - beta * ln( -ln( u ) );
	} // end FUNCTION gumbel2()

} // end FUNCTION factory()


// EXPORTS //

module.exports = factory;
