'use strict';

/**
* Gumbel distribution pseudorandom numbers.
*
* @module @stdlib/math/base/random/gumbel
*
* @example
* var rgumbel = require( '@stdlib/math/base/random/gumbel' );
*
* var v = rgumbel( 0.5, 1.0 );
* // returns <number>
*
* @example
* var factory = require( '@stdlib/math/base/random/gumbel' ).factory;
*
* var rgumbel = factory( 3.0, 2.0, {
* 	'seed': 297
* });
*
* var v = rgumbel();
* // returns <number>
*/

// MODULES //

var setReadOnly = require( '@stdlib/utils/define-read-only-property' );
var gumbel = require( './gumbel.js' );
var factory = require( './factory.js' );


// METHODS //

setReadOnly( gumbel, 'factory', factory );


// EXPORTS //

module.exports = gumbel;
