'use strict';

/**
* Hypergeometric distributed pseudorandom numbers.
*
* @module @stdlib/math/base/random/hypergeometric
*
* @example
* var rhypergeom = require( '@stdlib/math/base/random/hypergeometric' );
*
* var v = rhypergeom( 10, 10, 10 );
* // returns <number>
*
* @example
* var factory = require( '@stdlib/math/base/random/hypergeometric' ).factory;
*
* var rhypergeom = factory( 5, 3, 2, {
* 	'seed': 297
* });
*
* var v = rhypergeom();
* // returns <number>
*/

// MODULES //

var setReadOnly = require( '@stdlib/utils/define-read-only-property' );
var hypergeometric = require( './hypergeometric.js' );
var factory = require( './factory.js' );


// METHODS //

setReadOnly( hypergeometric, 'factory', factory );


// EXPORTS //

module.exports = hypergeometric;
