'use strict';

/**
* Returns a pseudorandom number drawn from an inverse gamma distribution.
*
* @private
* @param {Function} rgamma - PRNG to generate gamma distributed numbers
* @param {PositiveNumber} alpha - shape parameter
* @param {PositiveNumber} beta - scale parameter
* @returns {number} pseudorandom number
*/
function invgamma( rgamma, alpha, beta ) {
	return 1.0 / rgamma( alpha, beta );
} // end FUNCTION invgamma()


// EXPORTS //

module.exports = invgamma;
