'use strict';

/**
* Inverse-gamma distributed pseudorandom numbers.
*
* @module @stdlib/math/base/random/invgamma
*
* @example
* var rinvgamma = require( '@stdlib/math/base/random/invgamma' );
*
* var v = rinvgamma( 2.0, 3.0 );
* // returns <number>
*
* @example
* var factory = require( '@stdlib/math/base/random/invgamma' ).factory;
*
* var rinvgamma = factory( 3.0, 2.0, {
* 	'seed': 297
* });
*
* var v = rinvgamma();
* // returns <number>
*/

// MODULES //

var setReadOnly = require( '@stdlib/utils/define-read-only-property' );
var invgamma = require( './invgamma.js' );
var factory = require( './factory.js' );


// METHODS //

setReadOnly( invgamma, 'factory', factory );


// EXPORTS //

module.exports = invgamma;
