'use strict';

// MODULES //

var pow = require( '@stdlib/math/base/special/pow' );


// MAIN //

/**
* Returns a random number drawn from a Kumaraswamy's double bounded distribution.
*
* @private
* @param {Function} rand - pseudorandom number generator for uniformly distributed numbers
* @param {PositiveNumber} a - first shape parameter
* @param {PositiveNumber} b - second shape parameter
* @returns {Probability} pseudorandom number
*/
function sample( rand, a, b ) {
	var u = rand();
	return pow( 1.0 - pow( 1.0-u, 1.0/b ), 1.0/a );
} // end FUNCTION sample()


// EXPORTS //

module.exports = sample;
