'use strict';

/**
* A linear congruential pseudorandom number generator (LCG) whose output is shuffled.
*
* @module @stdlib/math/base/random/lcg-shuffle
*
* @example
* var lcg = require( '@stdlib/math/base/random/lcg-shuffle' );
*
* var v = lcg();
* // returns <number>
*
* @example
* var factory = require( '@stdlib/math/base/random/lcg' ).factory;
*
* var lcg = factory( 1234 );
*
* var v = lcg();
* // returns 0.6619843908874246
*/

// MODULES //

var setReadOnly = require( '@stdlib/utils/define-read-only-property' );
var lcg = require( './lcg_shuffled.js' );
var factory = require( './factory.js' );


// METHODS //

setReadOnly( lcg, 'factory', factory );


// EXPORTS //

module.exports = lcg;
