'use strict';

// MODULES //

var tape = require( 'tape' );
var lcg = require( '@stdlib/math/base/random/lcg' );
var isNumberArray = require( '@stdlib/utils/is-number' ).isPrimitiveNumberArray;
var createTable = require( './../lib/create_table.js' );


// TESTS //

tape( 'main export is a function', function test( t ) {
	t.ok( true, __filename );
	t.equal( typeof createTable, 'function', 'main export is a function' );
	t.end();
});

tape( 'the function returns an array of numbers', function test( t ) {
	var out = createTable( lcg );
	t.equal( isNumberArray( out ), true, 'returns an array of numbers' );
	t.end();
});

tape( 'if the provided LCG returns `NaN`, the function throws an error', function test( t ) {
	t.throws( foo, Error, 'throws an error' );
	t.end();

	function foo() {
		createTable( rand );
	}

	function rand() {
		return NaN;
	}
});
