'use strict';

// MODULES //

var tape = require( 'tape' );
var lcg = require( './../lib' );


// TESTS //

tape( 'main export is a function', function test( t ) {
	t.ok( true, __filename );
	t.equal( typeof lcg, 'function', 'main export is a function' );
	t.end();
});

tape( 'attached to the main export is a method to generate linear congruential pseudorandom number generator', function test( t ) {
	t.equal( typeof lcg.factory, 'function', 'has factory method' );
	t.end();
});

tape( 'attached to the main export is the generator seed', function test( t ) {
	t.equal( typeof lcg.SEED, 'number', 'has `SEED` property' );
	t.end();
});

tape( 'the function returns pseudorandom numbers strictly between 0 and 1', function test( t ) {
	var v;
	var i;
	for ( i = 0; i < 1e4; i++ ) {
		v = lcg();
		t.equal( typeof v, 'number', 'returns a number' );
		t.equal( v > 0.0 && v < 1.0, true, 'returns a number between 0 and 1' );
	}
	t.end();
});
