'use strict';

/**
* A linear congruential pseudorandom number generator (lcg).
*
* @module @stdlib/math/base/random/lcg
*
* @example
* var lcg = require( '@stdlib/math/base/random/lcg' );
*
* var v = lcg();
* // returns <number>
*
* @example
* var factory = require( '@stdlib/math/base/random/lcg' ).factory;
*
* var lcg = factory( 1234 );
*
* var v = lcg();
* // returns 0.009657739666131204
*/

// MODULES //

var setReadOnly = require( '@stdlib/utils/define-read-only-property' );
var lcg = require( './lcg.js' );
var factory = require( './factory.js' );


// METHODS //

setReadOnly( lcg, 'factory', factory );


// EXPORTS //

module.exports = lcg;
