'use strict';

// MODULES //

var setReadOnly = require( '@stdlib/utils/define-read-only-property' );
var isPositive = require( '@stdlib/utils/is-positive-number' ).isPrimitive;
var isNumber = require( '@stdlib/utils/is-number' ).isPrimitive;
var isObject = require( '@stdlib/utils/is-plain-object' );
var isnan = require( '@stdlib/utils/is-nan' );
var randu = require( '@stdlib/math/base/random/randu' );
var ln = require( '@stdlib/math/base/special/ln' );


// FACTORY //

/**
* Returns a pseudorandom number generator for generating random numbers from a logistic distribution.
*
* @param {number} [mu] - mean parameter
* @param {PositiveNumber} [s] - scale parameter
* @param {Options} [options] - function options
* @param {*} [options.seed] - pseudorandom number generator seed
* @throws {TypeError} mu argument must be a number primitive
* @throws {TypeError} s argument must be a positive number
* @throws {TypeError} options must be an object
* @returns {Function} pseudorandom number generator
*
* @example
* var logistic = factory( 0.0, 1.0 );
* var v = logistic();
* // returns <number>
*
* @example
* var logistic = factory( -3.0, 0.5, {
* 	'seed': 297
* });
* var v = logistic();
* // returns <number>
*/
function factory() {
	var nargs;
	var args;
	var opts;
	var rand;
	var ret;
	var mu;
	var s;

	args = arguments;
	nargs = args.length;
	if ( nargs === 0 ) {
		rand = randu.factory();
	}
	else {
		if ( isObject( args[ 0 ] ) ) {
			opts = args[ 0 ];
			rand = randu.factory( opts );
		}
		else {
			mu = args[ 0 ];
			s = args[ 1 ];
			if ( !isNumber( mu ) || isnan( mu ) ) {
				throw new TypeError( 'invalid input argument. `mu` must be a number primitive. Value: `' + mu + '`.' );
			}
			if ( !isPositive( s ) ) {
				throw new TypeError( 'invalid input argument. `s` must be a positive number. Value: `' + s + '`.' );
			}
			if ( nargs > 2 ) {
				opts = args[ 2 ];
				if ( !isObject( opts ) ) {
					throw new TypeError( 'invalid input argument. If supplied, third argument must be an options object. Value: `' + opts + '`.' );
				}
				rand = randu.factory( opts );
			} else {
				rand = randu.factory();
			}
		}
	}

	if ( mu !== void 0 ) {
		ret = logistic1;
	} else {
		ret = logistic2;
	}
	setReadOnly( ret, 'NAME', 'logistic' );
	setReadOnly( ret, 'SEED', rand.SEED );
	setReadOnly( ret, 'PRNG', rand );
	return ret;

	/**
	* Returns pseudorandom number drawn from a logistic distribution with bound parameters.
	*
	* @private
	* @returns {number} pseudorandom number
	*
	* @example
	* var v = logistic1();
	* // returns <number>
	*/
	function logistic1() {
		var u = rand();
		return mu + s * ln( u / ( 1.0 - u ) );
	} // end FUNCTION logistic1()

	/**
	* Returns pseudorandom number drawn from a logistic distribution.
	*
	* @private
	* @param {number} mu - mean parameter
	* @param {PositiveNumber} s - scale parameter
	* @throws {TypeError} mu argument must be a number primitive
	* @throws {TypeError} s argument must be a positive number
	* @returns {number} pseudorandom number
	*
	* @example
	* var v = logistic2( 0.0, 2.0 );
	* // returns <number>
	*/
	function logistic2( mu, s ) {
		var u;
		if ( !isNumber( mu ) || isnan( mu ) ) {
			throw new TypeError( 'invalid input argument. `mu` must be a number primitive. Value: `' + mu + '`.' );
		}
		if ( !isPositive( s ) ) {
			throw new TypeError( 'invalid input argument. `s` must be a positive number. Value: `' + s + '`.' );
		}
		u = rand();
		return mu + s * ln( u / ( 1.0 - u ) );
	} // end FUNCTION logistic2()

} // end FUNCTION factory()


// EXPORTS //

module.exports = factory;
