'use strict';

/**
* Logistic distribution pseudorandom numbers.
*
* @module @stdlib/math/base/random/logistic
*
* @example
* var logistic = require( '@stdlib/math/base/random/logistic' );
*
* var v = logistic( 0.5, 1.0 );
* // returns <number>
*
* @example
* var factory = require( '@stdlib/math/base/random/logistic' ).factory;
* var logistic = factory( 3.0, 2.0, {
* 	'seed': 297
* });
*
* var v = logistic();
* // returns <number>
*
* @example
* var factory = require( '@stdlib/math/base/random/logistic' ).factory;
* var logistic = factory({
* 	'seed': 297
* });
*
* var v = logistic( 3.0, 2.0 );
* // returns <number>
*/

// MODULES //

var setReadOnly = require( '@stdlib/utils/define-read-only-property' );
var logistic = require( './logistic.js' );
var factory = require( './factory.js' );


// METHODS //

setReadOnly( logistic, 'factory', factory );


// EXPORTS //

module.exports = logistic;
