'use strict';

// MODULES //

var setReadOnly = require( '@stdlib/utils/define-read-only-property' );
var isPositive = require( '@stdlib/utils/is-positive-number' ).isPrimitive;
var isNumber = require( '@stdlib/utils/is-number' ).isPrimitive;
var isObject = require( '@stdlib/utils/is-plain-object' );
var isnan = require( '@stdlib/utils/is-nan' );
var randn = require( '@stdlib/math/base/random/randn' );
var exp = require( '@stdlib/math/base/special/exp' );


// FACTORY //

/**
* Returns a pseudorandom number generator for generating random numbers from a lognormal distribution.
*
* @param {number} [mu] - location parameter
* @param {PositiveNumber} [sigma] - scale parameter
* @param {Options} [options] - function options
* @param {*} [options.seed] - pseudorandom number generator seed
* @throws {TypeError} mu argument must be a number primitive
* @throws {TypeError} sigma argument must be a positive number
* @throws {TypeError} options must be an object
* @returns {Function} pseudorandom number generator
*
* @example
* var lognormal = factory( 0.0, 1.0 );
* var v = lognormal();
* // returns <number>
*
* @example
* var lognormal = factory( -3.0, 0.5, {
*     'seed': 297
* });
* var v = lognormal();
* // returns <number>
*/
function factory() {
	var nargs;
	var sigma;
	var args;
	var opts;
	var rand;
	var ret;
	var mu;

	args = arguments;
	nargs = args.length;
	if ( nargs === 0 ) {
		rand = randn.factory();
	}
	else {
		if ( isObject( args[ 0 ] ) ) {
			opts = args[ 0 ];
			rand = randn.factory( opts );
		} else {
			mu = args[ 0 ];
			sigma = args[ 1 ];
			if ( !isNumber( mu ) || isnan( mu ) ) {
				throw new TypeError( 'invalid input argument. `mu` must be a number primitive. Value: `' + mu + '`.' );
			}
			if ( !isPositive( sigma ) ) {
				throw new TypeError( 'invalid input argument. `sigma` must be a positive number. Value: `' + sigma + '`.' );
			}
			if ( nargs > 2 ) {
				opts = args[ 2 ];
				if ( !isObject( opts ) ) {
					throw new TypeError( 'invalid input argument. If supplied, third argument must be an options object. Value: `' + opts + '`.' );
				}
				rand = randn.factory( opts );
			} else {
				rand = randn.factory();
			}
		}
	}

	if ( mu !== void 0 ) {
		ret = lognormal1;
	} else {
		ret = lognormal2;
	}
	setReadOnly( ret, 'NAME', 'lognormal' );
	setReadOnly( ret, 'SEED', rand.SEED );
	setReadOnly( ret, 'PRNG', rand );
	return ret;

	/**
	* Returns a log-normally distributed pseudorandom number with bound parameters `mu` and `sigma`.
	*
	* @private
	* @returns {PositiveNumber} pseudorandom number
	*
	* @example
	* var v = lognormal1();
	* // returns <number>
	*/
	function lognormal1() {
		var z = mu + sigma * rand();
		return exp( z );
	} // end FUNCTION lognormal1()

	/**
	* Returns a log-normally distributed pseudorandom number with parameters `mu` and `sigma`.
	*
	* @private
	* @param {number} mu - mean parameter
	* @param {PositiveNumber} sigma - standard deviation
	* @throws {TypeError} mu argument must be a number primitive
	* @throws {TypeError} sigma argument must be a positive number
	* @returns {PositiveNumber} pseudorandom number
	*
	* @example
	* var v = lognormal2( -2.0, 4.0 );
	* // returns <number>
	*/
	function lognormal2( mu, sigma ) {
		var z;
		if ( !isNumber( mu ) || isnan( mu ) ) {
			throw new TypeError( 'invalid input argument. `mu` must be a number primitive. Value: `' + mu + '`.' );
		}
		if ( !isPositive( sigma ) ) {
			throw new TypeError( 'invalid input argument. `sigma` must be a positive number. Value: `' + sigma + '`.' );
		}
		z = mu + sigma * rand();
		return exp( z );
	} // end FUNCTION lognormal2()

} // end FUNCTION factory()


// EXPORTS //

module.exports = factory;
