'use strict';

/**
* Log-normal distributed pseudorandom numbers.
*
* @module @stdlib/math/base/random/lognormal
*
* @example
* var lognormal = require( '@stdlib/math/base/random/lognormal' );
*
* var v = lognormal( 0.0, 1.0 );
* // returns <number>
*
* @example
* var factory = require( '@stdlib/math/base/random/lognormal' ).factory;
* var lognormal = factory( 3.0, 2.0, {
*     'seed': 297
* });
*
* var v = lognormal();
* // returns <number>
*
* @example
* var factory = require( '@stdlib/math/base/random/lognormal' ).factory;
* var lognormal = factory({
*     'seed': 297
* });
*
* var v = lognormal( 3.0, 2.0 );
* // returns <number>
*/

// MODULES //

var setReadOnly = require( '@stdlib/utils/define-read-only-property' );
var lognormal = require( './lognormal.js' );
var factory = require( './factory.js' );


// METHODS //

setReadOnly( lognormal, 'factory', factory );


// EXPORTS //

module.exports = lognormal;
