'use strict';

/**
* Returns a pseudorandom number drawn from a negative binomial distribution.
*
* @private
* @param {Function} rpois - PRNG for generating Poisson random variates
* @param {Function} rgamma - PRNG for generating Gamma random variates
* @param {PositiveNumber} r - number of failures until experiment is stopped
* @param {number} p - success probability
* @returns {NonNegativeInteger} pseudorandom number
*/
function negativeBinomial( rpois, rgamma, r, p ) {
	return rpois( rgamma( r, (1.0 - p)/p ) );
} // end FUNCTION negativeBinomial()


// EXPORTS //

module.exports = negativeBinomial;
