'use strict';

/**
* Negative binomial distributed pseudorandom numbers.
*
* @module @stdlib/math/base/random/negative-binomial
*
* @example
* var rnbinom = require( '@stdlib/math/base/random/negative-binomial' );
*
* var v = rnbinom( 20, 0.5 );
* // returns <number>
*
* @example
* var factory = require( '@stdlib/math/base/random/negative-binomial' ).factory;
*
* var rnbinom = factory( 10, 0.3, {
* 	'seed': 297
* });
*
* var v = rnbinom();
* // returns <number>
*/

// MODULES //

var setReadOnly = require( '@stdlib/utils/define-read-only-property' );
var negativeBinomial = require( './negative_binomial.js' );
var factory = require( './factory.js' );


// METHODS //

setReadOnly( negativeBinomial, 'factory', factory );


// EXPORTS //

module.exports = negativeBinomial;
