'use strict';

/**
* Normally distributed pseudorandom numbers.
*
* @module @stdlib/math/base/random/normal
*
* @example
* var rnorm = require( '@stdlib/math/base/random/normal' );
*
* var v = rnorm( 0.0, 1.0 );
* // returns <number>
*
* @example
* var factory = require( '@stdlib/math/base/random/normal' ).factory;
*
* var rnorm = factory( 3.0, 2.0, {
* 	'seed': 297
* });
*
* var v = rnorm();
* // returns <number>
*/

// MODULES //

var setReadOnly = require( '@stdlib/utils/define-read-only-property' );
var normal = require( './normal.js' );
var factory = require( './factory.js' );


// METHODS //

setReadOnly( normal, 'factory', factory );


// EXPORTS //

module.exports = normal;
