'use strict';

/**
* Pareto (Type I) distributed pseudorandom numbers.
*
* @module @stdlib/math/base/random/pareto-type1
*
* @example
* var pareto = require( '@stdlib/math/base/random/pareto-type1' );
*
* var v = pareto( 2.0, 3.0 );
* // returns <number>
*
* @example
* var factory = require( '@stdlib/math/base/random/pareto-type1' ).factory;
* var pareto = factory( 3.0, 2.0, {
* 	'seed': 297
* });
*
* var v = pareto();
* // returns <number>
*
* @example
* var factory = require( '@stdlib/math/base/random/pareto-type1' ).factory;
* var pareto = factory({
* 	'seed': 297
* });
*
* var v = pareto( 3.0, 2.0 );
* // returns <number>
*/

// MODULES //

var setReadOnly = require( '@stdlib/utils/define-read-only-property' );
var pareto = require( './pareto.js' );
var factory = require( './factory.js' );


// METHODS //

setReadOnly( pareto, 'factory', factory );


// EXPORTS //

module.exports = pareto;
