'use strict';

/**
* Poisson distributed pseudorandom numbers.
*
* @module @stdlib/math/base/random/poisson
*
* @example
* var poisson = require( '@stdlib/math/base/random/poisson' );
*
* var v = poisson( 4.0 );
* // returns <NonNegativeInteger>
*
* @example
* var factory = require( '@stdlib/math/base/random/poisson' ).factory;
*
* var poisson = factory( 4.0, {
* 	'seed': 297
* });
*
* var v = poisson();
* // returns <NonNegativeInteger>
*/

// MODULES //

var setReadOnly = require( '@stdlib/utils/define-read-only-property' );
var poisson = require( './poisson.js' );
var factory = require( './factory.js' );


// METHODS //

setReadOnly( poisson, 'factory', factory );


// EXPORTS //

module.exports = poisson;
