'use strict';

// MODULES //

var tape = require( 'tape' );
var round = require( '@stdlib/math/base/special/round' );
var factory = require( './../lib/factory.js' );


// TESTS //

tape( 'main export is a function', function test( t ) {
	t.ok( true, __filename );
	t.equal( typeof factory, 'function', 'main export is a function' );
	t.end();
});

tape( 'the function returns a pseudorandom number generator (no seed)', function test( t ) {
	var rpois;
	var r;
	var i;

	// When binding distribution parameters...
	rpois = factory( 2.0 );
	for ( i = 0; i < 100; i++ ) {
		r = rpois();
		t.equal( typeof r, 'number', 'returns a number' );
	}

	// Without binding distribution parameters...
	rpois = factory();
	for ( i = 0; i < 100; i++ ) {
		r = rpois( 100.0 );
		t.equal( typeof r, 'number', 'returns a number' );
	}

	t.end();
});

tape( 'the function returns a seeded pseudorandom number generator', function test( t ) {
	var rpois1;
	var rpois2;
	var seed;
	var r1;
	var r2;
	var i;

	seed = round( Date.now() / 1000 );

	rpois1 = factory( 35.0, {
		'seed': seed
	});
	rpois2 = factory( 35.0, {
		'seed': seed
	});

	t.notEqual( rpois1, rpois2, 'separate generators' );

	for ( i = 0; i < 100; i++ ) {
		r1 = rpois1();
		r2 = rpois2();
		t.equal( r1, r2, 'both return same number' );
	}
	t.end();
});

tape( 'attached to the returned function is the generator name', function test( t ) {
	var rpois = factory();
	t.equal( rpois.NAME, 'poisson', 'has `NAME` property' );
	t.end();
});

tape( 'attached to the returned function is the generator seed', function test( t ) {
	var rpois = factory({
		'seed': 12345
	});
	t.equal( typeof rpois.SEED, 'number', 'has `SEED` property' );
	t.equal( rpois.SEED, 12345, 'equal to provided seed' );
	t.end();
});

tape( 'the function throws an error if the `lambda` value is not a positive number value', function test( t ) {
	var values;
	var i;

	values = [
		-2.0,
		0.0,
		'5',
		null,
		true,
		undefined,
		NaN,
		[],
		function(){}
	];

	for ( i = 0; i < values.length; i++ ) {
		t.throws( badValue( values[i] ), TypeError, 'throws an error when provided '+values[i] );
	}
	t.end();

	function badValue( value ) {
		return function() {
			factory( value );
		};
	}
});

tape( 'the function throws an error if the `options` is not a simple object (when `lambda` is not supplied)', function test( t ) {
	var values;
	var i;

	values = [
		'abc',
		null,
		true,
		undefined,
		NaN,
		[],
		function(){}
	];

	for ( i = 0; i < values.length; i++ ) {
		t.throws( badValue( values[i] ), TypeError, 'throws an error when provided '+values[i] );
	}
	t.end();

	function badValue( value ) {
		return function() {
			factory( value );
		};
	}
});

tape( 'the function throws an error if the `options` is not a simple object (when `lambda` is supplied)', function test( t ) {
	var values;
	var i;

	values = [
		'abc',
		5,
		null,
		true,
		undefined,
		NaN,
		[],
		function(){}
	];

	for ( i = 0; i < values.length; i++ ) {
		t.throws( badValue( values[i] ), TypeError, 'throws an error when provided '+values[i] );
	}
	t.end();

	function badValue( value ) {
		return function() {
			factory( 10.0, value );
		};
	}
});

tape( 'when called without parameters, the function returns a generator that throws an error if the `lambda` value is not a positive number', function test( t ) {
	var values;
	var rpois;
	var i;

	rpois = factory();
	values = [
		-2.0,
		0.0,
		'5',
		null,
		true,
		undefined,
		NaN,
		[],
		{},
		function(){}
	];

	for ( i = 0; i < values.length; i++ ) {
		t.throws( badValue( values[i] ), TypeError, 'throws an error when provided '+values[i] );
	}
	t.end();

	function badValue( value ) {
		return function() {
			rpois( value );
		};
	}
});
