'use strict';

// MODULES //

var tape = require( 'tape' );
var randu = require( '@stdlib/math/base/random/randu' ).factory;
var isNonNegativeInteger = require( '@stdlib/math/base/utils/is-nonnegative-integer' );
var poisson = require( './../lib/rejection.js' );


// TESTS //

tape( 'main export is a function', function test( t ) {
	t.ok( true, __filename );
	t.equal( typeof poisson, 'function', 'main export is a function' );
	t.end();
});

tape( 'the function returns Poisson distributed pseudorandom numbers', function test( t ) {
	var lambda;
	var rand;
	var r;
	var i;

	rand = randu({
		'name': 'minstd-shuffle',
		'seed': 12345
	});
	for ( i = 0; i < 1e4; i++ ) {
		// Note: `10.0` is chosen to ensure all code paths are hit.
		lambda = rand() + 10.0;
		r = poisson( rand, lambda );
		t.strictEqual( isNonNegativeInteger( r ), true, 'returns a nonnegative integer' );
	}
	t.end();
});
