'use strict';

var randi = require( './../lib' );

var seed;
var rand;
var i;

// Generate pseudorandom numbers...
console.log( '\nseed: %d', randi.SEED );
for ( i = 0; i < 100; i++ ) {
	console.log( randi() );
}

// Create a new pseudorandom number generator...
seed = 1234;
rand = randi.factory({
	'seed': seed
});
console.log( '\nseed: %d', seed );
for ( i = 0; i < 100; i++ ) {
	console.log( rand() );
}

// Create another pseudorandom number generator using a previous seed...
rand = randi.factory({
	'seed': randi.SEED
});
console.log( '\nseed: %d', randi.SEED );
for ( i = 0; i < 100; i++ ) {
	console.log( rand() );
}
