'use strict';

/**
* Pseudorandom numbers having integer values.
*
* @module @stdlib/math/base/random/randi
*
* @example
* var randi = require( '@stdlib/math/base/random/randi' );
*
* var v = randi();
* // returns <number>
*
* @example
* var factory = require( '@stdlib/math/base/random/randi' ).factory;
*
* var randi = factory({
*     'name': 'minstd',
*     'seed': 12345
* });
*
* var v = randi();
* // returns <number>
*/

// MODULES //

var setReadOnly = require( '@stdlib/utils/define-read-only-property' );
var randi = require( './randi.js' );
var factory = require( './factory.js' );


// MAIN //

setReadOnly( randi, 'factory', factory );


// EXPORTS //

module.exports = randi;
