var isObject = require( '@stdlib/utils/is-object' ); // TODO: plain-object
var defaults = require( './defaults.json' );
var PRNGS = require( './prngs.js' );


// FACTORY //

/**
* Returns a pseudorandom number generator for numbers from a standard normal distribution.
*
* @param {Options} opts - function options
* @param {string} [opts.name='improved-ziggurat'] - name of pseudorandom number generator
* @param {*} [opts.seed] - pseudorandom number generator seed
* @throws {TypeError} must provide an object
* @throws {Error} must provide the name of a supported pseudorandom number generator
* @returns {Function} pseudorandom number generator
*
* @example
* var randn = factory();
* var v = randn();
* // returns <number>
*
* @example
* var randn = factory({
*     'name': 'box-muller'
* });
* var v = randn();
* // returns <number>
*
* @example
* var randn = factory({
*     'seed': 12345
* });
* var v = randn();
* // returns <number>
*
* @example
* var randn = factory({
*     'name': 'box-muller',
*     'seed': 12345
* });
* var v = randn();
* // returns <number>
*/
function factory( opts ) {
	var rand;
	var name;
	var prng;
	var seed;
	if ( arguments.length ) {
		if ( !isObject( opts ) ) {
			throw new TypeError( 'invalid input argument. Must provide an object. Value: `' + opts + '`.' );
		}
		if ( opts.hasOwnProperty( 'name' ) ) {
			name = opts.name;
		} else {
			name = defaults.name;
		}
		if ( opts.hasOwnProperty( 'seed' ) ) {
			seed = opts.seed;
		}
	} else {
		name = defaults.name;
	}
	prng = PRNGS[ name ];
	if ( prng === void 0 ) {
		throw new Error( 'invalid option. Unrecognized/unsupported PRNG. Option: `' + name + '`.' );
	}
	if ( seed === void 0 ) {
		rand = prng.factory();
	} else {
		rand = prng.factory({
			'seed':seed
		});
	}
	/**
	* Returns a pseudorandom number drawn from a standard normal distribution.
	*
	* @returns {number} pseudorandom number
	*
	* @example
	* var v = normal();
	* // returns <number>
	*/
	function normal() {
		return rand();
	} // end FUNCTION uniform()

	setReadOnly( normal, 'NAME', 'normal' );
	setReadOnly( normal, 'SEED', rand.SEED );
	setReadOnly( normal, 'MIN', rand.MIN );
	setReadOnly( normal, 'MAX', rand.MAX );
	setReadOnly( normal, 'PRNG', rand );

	return normal;
} // end FUNCTION factory()


// EXPORTS //

module.exports = factory;
