'use strict';

// MODULES //

var setReadOnly = require( '@stdlib/utils/define-read-only-property' );
var isPositive = require( '@stdlib/utils/is-positive-number' ).isPrimitive;
var isObject = require( '@stdlib/utils/is-plain-object' );
var randu = require( '@stdlib/math/base/random/randu' );
var sqrt = require( '@stdlib/math/base/special/sqrt' );
var ln = require( '@stdlib/math/base/special/ln' );


// FACTORY //

/**
* Returns a pseudorandom number generator for generating Rayleigh distributed random numbers.
*
* @param {PositiveNumber} [sigma] - scale parameter
* @param {Options} [opts] - function options
* @param {*} [opts.seed] - pseudorandom number generator seed
* @throws {TypeError} sigma argument must be a positive number
* @throws {TypeError} options must be an object
* @returns {Function} pseudorandom number generator
*
* @example
* var rayleigh = factory( 1.0 );
* var v = rayleigh();
* // returns <NonNegativeNumber>
*
* @example
* var rayleigh = factory( 0.5, {
* 	'seed': 297
* });
* var v = rayleigh();
* // returns <NonNegativeNumber>
*/
function factory() {
	var nargs;
	var sigma;
	var args;
	var opts;
	var rand;
	var ret;

	args = arguments;
	nargs = args.length;
	if ( nargs === 0 ) {
		rand = randu.factory();
	}
	else {
		if ( isObject( args[ 0 ] ) ) {
			opts = args[ 0 ];
			rand = randu.factory( opts );
		} else {
			sigma = args[ 0 ];
			if ( !isPositive( sigma ) ) {
				throw new TypeError( 'invalid input argument. First argument `sigma` must be a positive number. Value: `' + sigma + '`.' );
			}
			if ( nargs > 1 ) {
				opts = args[ 1 ];
				if ( !isObject( opts ) ) {
					throw new TypeError( 'invalid input argument. If supplied, third argument must be an options object. Value: `' + opts + '`.' );
				}
				rand = randu.factory( opts );
			} else {
				rand = randu.factory();
			}
		}
	}

	if ( sigma !== void 0 ) {
		ret = rayleigh1;
	}
	else {
		ret = rayleigh2;
	}
	setReadOnly( ret, 'NAME', 'rayleigh' );
	setReadOnly( ret, 'SEED', rand.SEED );
	setReadOnly( ret, 'PRNG', rand );
	return ret;

	/**
	* Returns a pseudorandom number from a Rayleigh distribution with bound scale parameter `sigma`.
	*
	* @private
	* @returns {NonNegativeNumber} pseudorandom number
	*
	* @example
	* var v = rayleigh1();
	* // returns <NonNegativeNumbers>
	*/
	function rayleigh1() {
		var u = rand();
		return sigma * sqrt( -2.0 * ln( u ) );
	} // end FUNCTION rayleigh1()

	/**
	* Returns a pseudorandom number from a Rayleigh distribution with scale parameter `sigma`.
	*
	* @private
	* @param {PositiveNumber} sigma - scale parameter
	* @throws {TypeError} sigma argument must be a positive number
	* @returns {NonNegativeNumber} pseudorandom number
	*
	* @example
	* var v = rayleigh2( 3.0 );
	* // returns <NonNegativeNumber>
	*/
	function rayleigh2( sigma ) {
		var u;
		if ( !isPositive( sigma ) ) {
			throw new TypeError( 'invalid input argument. First argument `sigma` must be a positive number. Value: `' + sigma + '`.' );
		}
		u = rand();
		return sigma * sqrt( -2.0 * ln( u ) );
	} // end FUNCTION rayleigh2()

} // end FUNCTION factory()


// EXPORTS //

module.exports = factory;
