'use strict';

// MODULES //

var tape = require( 'tape' );
var kstest = require( '@stdlib/math/generics/statistics/kstest' );
var round = require( '@stdlib/math/base/special/round' );
var factory = require( './../lib/factory.js' );


// TESTS //

tape( 'main export is a function', function test( t ) {
	t.ok( true, __filename );
	t.equal( typeof factory, 'function', 'main export is a function' );
	t.end();
});

tape( 'the function returns a pseudorandom number generator (no seed)', function test( t ) {
	var rrayleigh;
	var r;
	var i;

	// When binding distribution parameter...
	rrayleigh = factory( 2.0 );
	for ( i = 0; i < 100; i++ ) {
		r = rrayleigh();
		t.equal( typeof r, 'number', 'returns a number' );
	}

	// Without binding distribution parameter...
	rrayleigh = factory();
	for ( i = 0; i < 100; i++ ) {
		r = rrayleigh( 10.0 );
		t.equal( typeof r, 'number', 'returns a number' );
	}

	t.end();
});

tape( 'the function returns a seeded pseudorandom number generator', function test( t ) {
	var rrayleigh1;
	var rrayleigh2;
	var seed;
	var r1;
	var r2;
	var i;

	seed = round( Date.now() / 1000 );

	rrayleigh1 = factory( 3.5, {
		'seed': seed
	});
	rrayleigh2 = factory( 3.5, {
		'seed': seed
	});

	t.notEqual( rrayleigh1, rrayleigh2, 'separate generators' );

	for ( i = 0; i < 100; i++ ) {
		r1 = rrayleigh1();
		r2 = rrayleigh2();
		t.equal( r1, r2, 'both return same number' );
	}
	t.end();
});

tape( 'attached to the returned function is the generator name', function test( t ) {
	var rrayleigh = factory();
	t.equal( rrayleigh.NAME, 'rayleigh', 'has `NAME` property' );
	t.end();
});

tape( 'attached to the returned function is the generator seed', function test( t ) {
	var rrayleigh = factory({
		'seed': 12345
	});
	t.equal( typeof rrayleigh.SEED, 'number', 'has `SEED` property' );
	t.equal( rrayleigh.SEED, 12345, 'equal to provided seed' );
	t.end();
});

tape( 'the function throws an error if the `sigma` value is not a positive number value', function test( t ) {
	var values;
	var i;

	values = [
		-2.0,
		0.0,
		'5',
		null,
		true,
		undefined,
		NaN,
		[],
		function(){}
	];

	for ( i = 0; i < values.length; i++ ) {
		t.throws( badValue( values[i] ), TypeError, 'throws an error when provided '+values[i] );
	}
	t.end();

	function badValue( value ) {
		return function() {
			factory( value );
		};
	}
});

tape( 'the function throws an error if the `options` is not a simple object (when `lambda` is not supplied)', function test( t ) {
	var values;
	var i;

	values = [
		'abc',
		null,
		true,
		undefined,
		NaN,
		[],
		function(){}
	];

	for ( i = 0; i < values.length; i++ ) {
		t.throws( badValue( values[i] ), TypeError, 'throws an error when provided '+values[i] );
	}
	t.end();

	function badValue( value ) {
		return function() {
			factory( value );
		};
	}
});

tape( 'the function throws an error if the `options` is not a simple object (when `sigma` is supplied)', function test( t ) {
	var values;
	var i;

	values = [
		'abc',
		5,
		null,
		true,
		undefined,
		NaN,
		[],
		function(){}
	];

	for ( i = 0; i < values.length; i++ ) {
		t.throws( badValue( values[i] ), TypeError, 'throws an error when provided '+values[i] );
	}
	t.end();

	function badValue( value ) {
		return function() {
			factory( 10.0, value );
		};
	}
});

tape( 'when called without parameters, the function returns a generator that throws an error if the `sigma` value is not a positive number', function test( t ) {
	var values;
	var rrayleigh;
	var i;

	rrayleigh = factory();
	values = [
		-2.0,
		0.0,
		'5',
		null,
		true,
		undefined,
		NaN,
		[],
		{},
		function(){}
	];

	for ( i = 0; i < values.length; i++ ) {
		t.throws( badValue( values[i] ), TypeError, 'throws an error when provided '+values[i] );
	}
	t.end();

	function badValue( value ) {
		return function() {
			rrayleigh( value );
		};
	}
});

