'use strict';

/**
* Student's t distributed pseudorandom numbers.
*
* @module @stdlib/math/base/random/t
*
* @example
* var t = require( '@stdlib/math/base/random/t' );
*
* var v = t( 2.0 );
* // returns <number>
*
* @example
* var factory = require( '@stdlib/math/base/random/t' ).factory;
*
* var t = factory( 3.0, {
* 	'seed': 297
* });
*
* var v = t();
* // returns <number>
*/

// MODULES //

var setReadOnly = require( '@stdlib/utils/define-read-only-property' );
var t = require( './t.js' );
var factory = require( './factory.js' );


// METHODS //

setReadOnly( t, 'factory', factory );


// EXPORTS //

module.exports = t;
