'use strict';

/**
* Triangular distribution pseudorandom numbers.
*
* @module @stdlib/math/base/random/triangular
*
* @example
* var rtriang = require( '@stdlib/math/base/random/triangular' );
*
* var v = rtriang( 0.0, 10.0, 7.0 );
* // returns <number>
*
* @example
* var factory = require( '@stdlib/math/base/random/triangular' ).factory;
*
* var rtriang = factory( -5.0, 5.0, 3.0, {
* 	'seed': 297
* });
*
* var v = rtriang();
* // returns <number>
*/

// MODULES //

var setReadOnly = require( '@stdlib/utils/define-read-only-property' );
var triangular = require( './triangular.js' );
var factory = require( './factory.js' );


// METHODS //

setReadOnly( triangular, 'factory', factory );


// EXPORTS //

module.exports = triangular;
