'use strict';

/**
* Triangular distributed pseudorandom numbers.
*
* @module @stdlib/math/base/random/triangular
*
* @example
* var triangular = require( '@stdlib/math/base/random/triangular' );
*
* var v = triangular( 0.0, 10.0, 7.0 );
* // returns <number>
*
* @example
* var factory = require( '@stdlib/math/base/random/triangular' ).factory;
* var triangular = factory( -5.0, 5.0, 3.0, {
*     'seed': 297
* });
*
* var v = triangular();
* // returns ~0.213
*
* @example
* var factory = require( '@stdlib/math/base/random/triangular' ).factory;
* var triangular = factory({
*     'seed': 297
* });
*
* var v = triangular( -5.0, 5.0, 3.0 );
* // returns ~0.213
*/

// MODULES //

var setReadOnly = require( '@stdlib/utils/define-read-only-property' );
var triangular = require( './triangular.js' );
var factory = require( './factory.js' );


// METHODS //

setReadOnly( triangular, 'factory', factory );


// EXPORTS //

module.exports = triangular;
