tape( 'the function returns a function for creating random numbers from a uniform distribution', opts, function test( t ) {
	var rejected;
	var uniform;
	var pValue;
	var N;
	var a;
	var b;
	var i;
	var j;
	var x;

	a = 2.0;
	b = 4.0;
	uniform = factory( a, b, {
		'seed': 12345
	});
	rejected = 0;
	x = new Array( 1e3 );
	N = 300;
	for ( i = 0; i < N; i++ ) {
		for ( j = 0; j < x.length; j++ ) {
			x[ j ] = uniform();
			if ( x[ j ] < a || x[ j ] > b ) {
				t.ok( false, 'returned a number outside support: '+x[ j ] );
			}
		}
		// Test using Kolmogorov-Smirnov goodness-of-fit test:
		pValue = kstest( x, 'uniform', 2.0, 4.0 ).pValue;
		t.equal( typeof pValue, 'number', 'returns a p-value' );
		if ( pValue < 0.05 ) {
			rejected += 1;
		}
	}
	// Account for small sample sizes and few repeats...
	t.ok( rejected / N < 0.07, 'null hypothesis (i.e., that numbers are drawn from Uniform(2,4)) is rejected in less than 7% of cases ('+rejected+' of '+N+')' );
	t.end();
});
