'use strict';

var round = require( '@stdlib/math/base/special/round' );
var abs = require( './../lib' );

var rand;
var sign;
var i;

for ( i = 0; i < 100; i++ ) {
	rand = round( Math.random() * 100 ) - 50;
	sign = Math.random();
	if ( sign < 0.5 ) {
		sign = -1;
	} else {
		sign = 1;
	}
	console.log( 'abs(%d) = %d', rand, abs( rand ) );
}
