/**
* Compute the absolute value.
*/
#include "abs.h"

/**
* Computes the absolute value.
*
* @param x       number
* @return        absolute value
*/
double stdlib_base_abs( const double x ) {
	if ( x < 0.0 ) {
		return -x;
	}
	if ( x == 0.0 ) {
		return 0.0; // handles negative zero
	}
	return x;
}
