'use strict';

// MODULES //

var tape = require( 'tape' );
var isPositiveZero = require( '@stdlib/math/base/utils/is-positive-zero' );
var isnan = require( '@stdlib/math/base/utils/is-nan' );
var PINF = require( '@stdlib/math/constants/float64-pinf' );
var NINF = require( '@stdlib/math/constants/float64-ninf' );
var PI = require( '@stdlib/math/constants/float64-pi' );
var abs = require( './../lib' );


// TESTS //

tape( 'main export is a function', function test( t ) {
	t.ok( true, __filename );
	t.equal( typeof abs, 'function', 'main export is a function' );
	t.end();
});

tape( 'function computes the absolute value of a number', function test( t ) {
	t.equal( abs( -2 ), 2, 'negative number' );
	t.equal( abs( 3 ), 3, 'positive number' );
	t.equal( abs( 0 ), 0, 'zero' );
	t.equal( abs( -PI ), PI, 'pi' );
	t.end();
});

tape( 'function computes the absolute value of negative zero', function test( t ) {
	t.equal( isPositiveZero( abs( -0 ) ), true, 'returns positive zero' );
	t.end();
});

tape( 'function computes the absolute value of infinity', function test( t ) {
	t.equal( abs( PINF ), PINF, 'returns +infinity' );
	t.equal( abs( NINF ), PINF, 'returns +infinity' );
	t.end();
});

tape( 'if provided `NaN`, the function returns `NaN`', function test( t ) {
	var v = abs( NaN );
	t.equal( isnan( v ), true, 'returns NaN' );
	t.end();
});

