#!/usr/bin/env julia

import JSON

"""
    gen( x, filepath )

Generate fixture data and write to file.

# Arguments

* `x`: domain
* `filepath::AbstractString`: filepath of the output file

# Examples

``` julia
julia> x = linspace( -1000, 1000, 2001 );
julia> gen( x, \"./data.json\" );
```
"""
function gen( x, filepath )
	y = acosh( x );
	data = Dict([
		("x", x),
		("expected", y)
	]);
	outfile = open( filepath, "w" );
	write( outfile, JSON.json(data) );
	close( outfile );
end

# Get the filename:
file = @__FILE__;

# Extract the directory in which this file resides:
dir = dirname( file );

# Positive medium values:
x = linspace( 1.0, 3.0, 500 );
out = joinpath( dir, "medium_positive.json" );
gen( x, out );

# Large positive values:
x = linspace( 3.0, 28.0, 500 );
out = joinpath( dir, "large_positive.json" );
gen( x, out );

# Larger positive values:
x = linspace( 28.0, 100.0, 500 );
out = joinpath( dir, "larger_positive.json" );
gen( x, out );
