'use strict';

/**
* Evaluate the arctangent of a number.
*
* @module @stdlib/math/base/special/atan
*
* @example
* var v = atan( 0.0 );
* // returns ~0.0
*
* v = atan( -Math.PI/4.0 );
* // returns ~-1.0
*
* v = atan( Math.PI/4.0 );
* // returns ~1.0
*
* v = atan( NaN );
* // returns NaN
*/

// MODULES //

var atan = require( './atan.js' );


// EXPORTS //

module.exports = atan;
