'use strict';

var randu = require( '@stdlib/math/base/random/randu' );
var atan2 = require( './../lib' );

var x;
var y;
var i;

for ( i = 0; i < 100; i++ ) {
	y = randu() * 100;
	x = randu() * 100;
	console.log( 'y: %d, \t x: %d, \t atan2(y,x): %d',
		y.toFixed( 4 ),
		x.toFixed( 4 ),
		atan2( y, x ).toFixed( 4 )
	);
}
