#!/usr/bin/env julia

import JSON

"""
    gen( x, y, name )

Generate fixture data and write to file.

# Arguments

* `x`: denominator domain
* `y`: numerator domain
* `name::AbstractString`: output filename

# Examples

``` julia
julia> x = linspace( -1000.0, 1000.0, 2001 );
julia> y = linspace( 0.0, 1000.0, 1001 );
julia> gen( x, y, \"data.json\" );
```
"""
function gen( x, y, name )
	z = Array( Float64, length(x) );
	for i in eachindex(x)
		z[ i ] = atan2( y[i], x[i] );
	end

	# Store data to be written to file as a collection:
	data = Dict([
		("x", x),
		("y", y),
		("expected", z)
	]);

	# Based on the script directory, create an output filepath:
	filepath = joinpath( dir, name );

	# Write the data to the output filepath as JSON:
	outfile = open( filepath, "w" );
	write( outfile, JSON.json(data) );
	close( outfile );
end

# Get the filename:
file = @__FILE__;

# Extract the directory in which this file resides:
dir = dirname( file );

# Random (x positive, y positive):
x = rand( 5001 ) * 500.0;
y = rand( 5001 ) * 500.0;
gen( x, y, "positive_positive.json" );

# Random (x negative, y positive):
x = -rand( 1001 ) * 500.0;
y = rand( 1001 ) * 500.0;
gen( x, y, "negative_positive.json" );

# Random (x negative, y negative):
x = -rand( 1001 ) * 500.0;
y = -rand( 1001 ) * 500.0;
gen( x, y, "negative_negative.json" );
