# To run this script, `cd` to the `./benchmark/fixtures` directory and then, from the R shell, `source("./runner.R")`.
options( digits = 16 );
library( jsonlite );

set.seed( 329 )

x = sample( seq( 0.5, 100, by = 0.25 ), 1000, replace = TRUE )
y = sample( seq( 0.5, 100, by = 0.25 ), 1000, replace = TRUE )
v = beta( x, y )

cat( v, sep = ",\n" )

write( toJSON( x, digits = 16, auto_unbox = TRUE ), "./arg1.json" )
write( toJSON( y, digits = 16, auto_unbox = TRUE ), "./arg2.json" )
write( toJSON( v, digits = 16, auto_unbox = TRUE ), "./expected.json" )
